(4, 2, 'PHP实现获取全路径文件的扩展名', '201704/thumb_1491806230_758599867.jpg', '&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\r\n//PHP实现获取全路径文件的扩展名\r\n$path&amp;nbsp;=&amp;nbsp;&amp;nbsp;str_replace(&amp;#39;\\\\&amp;#39;,&amp;nbsp;&amp;#39;/&amp;#39;,&amp;nbsp;__FILE__);\r\n\r\nfunction&amp;nbsp;ext_1($path){\r\n	return&amp;nbsp;ltrim(strrchr($path,&amp;#39;.&amp;#39;),&amp;#39;.&amp;#39;);\r\n}\r\n\r\nfunction&amp;nbsp;ext_2($path){\r\n	return&amp;nbsp;substr($path,strrpos($path,&amp;#39;.&amp;#39;)+1);\r\n}\r\n\r\nfunction&amp;nbsp;ext_3($path){\r\n	$array&amp;nbsp;=&amp;nbsp;explode(&amp;#39;.&amp;#39;,$path);\r\n	return&amp;nbsp;array_pop($array);\r\n}\r\n\r\nfunction&amp;nbsp;ext_4($path){\r\n	$array&amp;nbsp;=&amp;nbsp;pathinfo($path);\r\n	return&amp;nbsp;$array[&amp;#39;extension&amp;#39;];\r\n}\r\n\r\nfunction&amp;nbsp;ext_5($path){\r\n	$path&amp;nbsp;=&amp;nbsp;basename($path);\r\n	$pattern&amp;nbsp;=&amp;nbsp;&amp;#39;/^[^\\.]+\\.([\\w]+)$/&amp;#39;;\r\n	return&amp;nbsp;preg_replace($pattern,&amp;#39;${1}&amp;#39;,$path);\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', 'PHP实现获取全路径文件的扩展名', 1491806230, 1, 0, 0, 0, 1),
(5, 2, '二维数组的排序', '201704/thumb_1491806344_1411861923.jpg', '&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;/**\r\n&amp;nbsp;*&amp;nbsp;排序二维数组&amp;nbsp;\r\n&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;&amp;nbsp;array&amp;nbsp;$array&amp;nbsp;需要排序的数组\r\n&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;string&amp;nbsp;$row&amp;nbsp;根据哪一列排序\r\n&amp;nbsp;*/\r\n\r\nfunction&amp;nbsp;array_sort($array,$row,$type=&amp;#39;asc&amp;#39;){\r\n	$array_temp&amp;nbsp;=&amp;nbsp;array();\r\n	foreach($array&amp;nbsp;as&amp;nbsp;$val){\r\n		$array_temp[$val[$row]]&amp;nbsp;=&amp;nbsp;$val;\r\n	}\r\n	\r\n	if($type&amp;nbsp;==&amp;nbsp;&amp;#39;asc&amp;#39;&amp;nbsp;||&amp;nbsp;!isset($type)){\r\n		ksort($array_temp);\r\n	}elseif($type&amp;nbsp;==&amp;nbsp;&amp;#39;desc&amp;#39;){\r\n		krsort($array_temp);\r\n	}\r\n	return&amp;nbsp;$array_temp;\r\n}\r\n\r\n$array&amp;nbsp;=&amp;nbsp;array(\r\n	&amp;nbsp;array(&amp;#39;name&amp;#39;=&amp;gt;&amp;#39;xiaoming&amp;#39;,&amp;#39;age&amp;#39;=&amp;gt;&amp;#39;18&amp;#39;,&amp;#39;hobby&amp;#39;=&amp;gt;&amp;quot;tv&amp;quot;),\r\n	&amp;nbsp;array(&amp;#39;name&amp;#39;=&amp;gt;&amp;#39;wangwu&amp;#39;,&amp;#39;age&amp;#39;=&amp;gt;&amp;#39;15&amp;#39;,&amp;#39;hobby&amp;#39;=&amp;gt;&amp;quot;play&amp;quot;),\r\n	&amp;nbsp;array(&amp;#39;name&amp;#39;=&amp;gt;&amp;#39;heihei&amp;#39;,&amp;#39;age&amp;#39;=&amp;gt;&amp;#39;20&amp;#39;,&amp;#39;hobby&amp;#39;=&amp;gt;&amp;quot;ball&amp;quot;),\r\n	&amp;nbsp;array(&amp;#39;name&amp;#39;=&amp;gt;&amp;#39;zhangsan&amp;#39;,&amp;#39;age&amp;#39;=&amp;gt;&amp;#39;13&amp;#39;,&amp;#39;hobby&amp;#39;=&amp;gt;&amp;quot;dance&amp;quot;)\r\n	);\r\n\r\n//第3个参数不填也是正序排\r\nprint_r(array_sort($array,&amp;#39;name&amp;#39;,&amp;#39;desc&amp;#39;));&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', '二维数组的排序', 1491806344, 1, 0, 0, 0, 1),
(6, 2, 'PHP中全局变量$_SERVER的常见元素', '201704/thumb_1491806722_921307791.png', '&lt;p&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;1、&lt;/span&gt;&lt;strong style=&quot;font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;$_SERVER[&amp;#39;PHP_SELF&amp;#39;]&lt;/strong&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;:当前执行脚本的文件名。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;比如：请求地址为 http://example.com/test.php ，那么使用$_SERVER[&amp;#39;PHP_SELF&amp;#39;]将得到 /test.php&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;2、&lt;/span&gt;&lt;strong style=&quot;font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;$_SERVER[&amp;#39;SERVER_ADDR&amp;#39;]&lt;/strong&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;:当前运行脚本所在的服务器的ip地址。&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;3、&lt;/span&gt;&lt;strong style=&quot;font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;$_SERVER[&amp;#39;SCRIPT_NAME&amp;#39;]&lt;/strong&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;:当前运行脚本所在的服务器的主机名。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;比如：请求地址为 http://www.example.com/test.php ，那么使用$_SERVER[&amp;#39;SERVER_NAME&amp;#39;]将得到 www.example.com&lt;/span&gt;&lt;br/&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background