(7, 2, 'php无乱码截取字符串', '201704/thumb_1491806784_437668800.png', '&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;/**&amp;nbsp;\r\n&amp;nbsp;*&amp;nbsp;[sub_utf8&amp;nbsp;utf-8无乱码截取字符串]\r\n&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;[string]&amp;nbsp;$str&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[要截取的字符串]\r\n&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;[int]&amp;nbsp;$start&amp;nbsp;&amp;nbsp;[从start位置开始截取，从0开始]\r\n&amp;nbsp;*&amp;nbsp;@param&amp;nbsp;&amp;nbsp;[type]&amp;nbsp;$length&amp;nbsp;[要截取的字符串长度]\r\n&amp;nbsp;*&amp;nbsp;@return&amp;nbsp;[string]&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;[截取到的字符串]\r\n&amp;nbsp;*/\r\nfunction&amp;nbsp;sub_utf8($str,$start,$length&amp;nbsp;=&amp;nbsp;null){\r\n	return&amp;nbsp;implode(array_slice(preg_split(&amp;#39;//u&amp;#39;,$str,-1,PREG_SPLIT_NO_EMPTY),$start,$length));\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', 'php无乱码截取字符串', 1491806784, 1, 0, 0, 0, 1),
(8, 2, 'PHP工具类之分页类', '201704/thumb_1491806877_569499420.jpg', '&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php&amp;nbsp;\r\n//分页类&amp;nbsp;\r\nClass&amp;nbsp;Page{&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;$_total;&amp;nbsp;//总记录数&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;$_pageSize;&amp;nbsp;//每页显示条数&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;$_limit;&amp;nbsp;//limit&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;$_page;&amp;nbsp;//当前页码&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;$_pageCount;&amp;nbsp;//总页码&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;$_url;&amp;nbsp;//地址&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private&amp;nbsp;$_sideNum&amp;nbsp;=&amp;nbsp;2;&amp;nbsp;//两边保持分页的量&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//构造方法初始化&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;__construct($total,$pageSize){&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;_total&amp;nbsp;=&amp;nbsp;isset($total)&amp;nbsp;?&amp;nbsp;$total&amp;nbsp;:&amp;nbsp;50;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;_pageSize&amp;nbsp;=&amp;nbsp;isset($pageSize)&amp;nbsp;?&amp;nbsp;$pageSize&amp;nbsp;:&amp;nbsp;5;\r\n		$this-&amp;gt;_pageCount&amp;nbsp;=&amp;nbsp;ceil($this-&amp;gt;_total/$this-&amp;gt;_pageSize);\r\n		$this-&amp;gt;_page&amp;nbsp;=&amp;nbsp;$this-&amp;gt;setPage();\r\n		$this-&amp;gt;_limit&amp;nbsp;=&amp;nbsp;&amp;quot;LIMIT&amp;nbsp;&amp;quot;.(($this-&amp;gt;_page&amp;nbsp;-&amp;nbsp;1)&amp;nbsp;*&amp;nbsp;($this-&amp;gt;_pageSize)&amp;nbsp;).&amp;quot;,&amp;nbsp;{$this-&amp;gt;_pageSize}&amp;quot;;\r\n		$this-&amp;gt;_url&amp;nbsp;=&amp;nbsp;$this-&amp;gt;setUrl();\r\n	}\r\n\r\n	public&amp;nbsp;&amp;nbsp;function&amp;nbsp;__get($key){\r\n		return&amp;nbsp;$this-&amp;gt;$key;\r\n	}\r\n\r\n	/*\r\n		获取当前页码\r\n	&amp;nbsp;*/\r\n	private&amp;nbsp;function&amp;nbsp;setPage(){\r\n		if(!empty($_GET[&amp;#39;page&amp;#39;])){\r\n			if($_GET[&amp;#39;page&amp;#39;]&amp;nbsp;&amp;gt;&amp;nbsp;0){\r\n				if($_GET[&amp;#39;page&amp;#39;]&amp;nbsp;&amp;gt;&amp;nbsp;$this-&amp;gt;_pageCount){\r\n					return&amp;nbsp;$this-&amp;gt;_pageCount;		\r\n				}else{\r\n					return&amp;nbsp;$_GET[&amp;#39;page&amp;#39;];\r\n				}\r\n			}else{\r\n				return&amp;nbsp;1;\r\n			}\r\n		}else{\r\n			return&amp;nbsp;1;\r\n		}\r\n	}\r\n\r\n	/*\r\n		获取地址\r\n	&amp;nbsp;*/\r\n	private&amp;nbsp;function&amp;nbsp;setUrl(){\r\n		$url&amp;nbsp;=&amp;nbsp;$_SERVER[&amp;#39;REQUEST_URI&amp;#39;];\r\n		$url_arr&amp;nbsp;=&amp;nbsp;parse_url($url);\r\n		if(isset($url_arr[&amp;#39;query&amp;#39;])){\r\n			parse_str($url_arr[&amp;#39;query&amp;#39;],$query);\r\n			unset($query[&amp;#39;page&amp;#39;]);\r\n			$url&amp;nbsp;=&amp;nbsp;$url_arr[&amp;#39;path&amp;#39;].&amp;#39;?&amp;#39;.http_build_query($query);\r\n		}\r\n		return&amp;nbsp;$url;\r\n	}\r\n\r\n	/*\r\n		数字目录\r\n	&amp;nbsp;*/\r\n	private&amp;nbsp;function&amp;nbsp;pageList(){\r\n		$pageList&amp;nbsp;=&amp;nbsp;&amp;#39;&amp;#39;;\r\n		//当前页左边的内容\r\n		for($i&amp;nbsp;=&amp;nbsp;$this-&amp;gt;_sideNum;&amp;nbsp;$i&amp;gt;0;&amp;nbsp;$i--){\r\n			$page&amp;nbsp;=&amp;nbsp;$this-&amp;gt;_page&amp;nbsp;-&amp;nbsp;$i;\r\n			if($page&amp;nbsp;&amp;lt;=&amp;nbsp;1)&amp;nbsp;continue;\r\n			$pageList&amp;nbsp;.=&amp;nbsp;&amp;quot;&amp;lt;a&amp;nbsp;href&amp;nbsp;=&amp;#39;&amp;quot;.$this-&amp;gt;_url.&amp;quot;&amp;amp;page=&amp;quot;.$page.&amp;quot;&amp;#39;&amp;gt;&amp;quot;.$page.&amp;quot;&amp;lt;/a&amp;gt;&amp;quot;;\r\n		}\r\n		//当前页\r\n		$pageList&amp;nbsp;.=&amp;nbsp;&amp;quot;&amp;lt;a&amp;nbsp;href=&amp;#39;&amp;quot;.$this-&amp;gt;_url.&amp;quot;&amp;amp;page=&amp;quot;.$this-&amp;gt;_page.&amp;quot;&amp;#39;&amp;nbsp;class=&amp;#39;current&amp;#39;&amp;gt;&amp;quot;.$this-&amp;gt;_page.&amp;quot;&amp;lt;/a&amp;gt;&amp;quot;;\r\n		//当前页右边的内容\r\n		for($i&amp;nbsp;=&amp;nbsp;1;&amp;nbsp;$i&amp;nbsp;&amp;lt;=&amp;nbsp;$this-&amp;gt;_sideNum;&amp;nbsp;$i++){\r\n			$page&amp;nbsp;=&amp;nbsp;$this-&amp;gt;_page&amp;nbsp;+&amp;nbsp;$i;\r\n			if($page&amp;nbsp;&amp;gt;=&amp;nbsp;$this-&amp;gt;_pageCount)&amp;nbsp;continue;\r\n			$pageList&amp;nbsp;.=&amp;nbsp;&amp;quot;&amp;lt;a&amp;nbsp;href=&amp;#39;&amp;quot;.$this-&amp;gt;_url.&amp;quot;&amp;amp;page=&amp;quot;.$page.&amp;quot;&amp;#39;&amp;gt;&amp;quot;.$page.&amp;quot;&amp;lt;/a&amp;gt;&amp;quot;;\r\n		}\r\n		return&amp;nbsp;$pageList;\r\n	}\r\n\r\n	/*\r\n		首页\r\n	&amp;nbsp;*/\r\n	private&amp;nbsp;function&amp;nbsp;first(){\r\n		return&amp;nbsp;&amp;quot;&amp;lt;a&amp;nbsp;href=&amp;#39;&amp;quot;.$this-&amp;gt;_url.&amp;quot;&amp;amp;page=1&amp;#39;&amp;gt;首页&amp;lt;/a&amp;gt;...&amp;quot;;\r\n	}\r\n\r\n	/*\r\n		尾页\r\n	&amp;nbsp;*/\r\n	private&amp;nbsp;function&amp;nbsp;last(){\r\n		return&amp;nbsp;&amp;quot;...&amp;lt;a&amp;nbsp;href=&amp;#39;&amp;quot;.$this-&amp;gt;_url.&amp;quot;&amp;amp;page=&amp;quot;.$this-&amp;gt;_pageCount.&amp;quot;&amp;#39;&amp;gt;尾页&amp;lt;/a&amp;gt;&amp;quot;;\r\n	}\r\n\r\n	/*\r\n		上一页\r\n	&amp;nbsp;*/\r\n	private&amp;nbsp;function&amp;nbsp;prev(){\r\n		if($this-&amp;gt;_page&amp;nbsp;&amp;gt;&amp;nbsp;1){\r\n			return&amp;nbsp;&amp;quot;&amp;lt;a&amp;nbsp;href=&amp;#39;&amp;quot;.$this-&amp;gt;_url.&amp;quot;&amp;amp;page=&amp;quot;.($this-&amp;gt;_page-1).&amp;quot;&amp;#39;&amp;gt;上一页&amp;lt;/a&amp;gt;&amp;quot;;\r\n		}\r\n	}\r\n\r\n	/*\r\n		下一页\r\n	&amp;nbsp;*/\r\n	private&amp;nbsp;function&amp;nbsp;next(){\r\n		if($this-&amp;gt;_page&amp;nbsp;&amp;lt;&amp;nbsp;$this-&amp;gt;_pageCount){\r\n			return&amp;nbsp;&amp;quot;&amp;lt;a&amp;nbsp;href=&amp;#39;&amp;quot;.$this-&amp;gt;_url.&amp;quot;&amp;amp;page=&amp;quot;.($this-&amp;gt;_page&amp;nbsp;+&amp;nbsp;1).&amp;quot;&amp;#39;&amp;gt;下一页&amp;lt;/a&amp;gt;&amp;quot;;\r\n		}\r\n	}\r\n\r\n	/*\r\n		展示分页信息\r\n	&amp;nbsp;*/\r\n	public&amp;nbsp;function&amp;nbsp;showPage(){\r\n		$page&amp;nbsp;=&amp;nbsp;&amp;#39;&amp;#39;;\r\n		$page&amp;nbsp;.=&amp;nbsp;$this-&amp;gt;first();&amp;nbsp;\r\n		$page&amp;nbsp;.=&amp;nbsp;$this-&amp;gt;pageList();\r\n		$page&amp;nbsp;.=&amp;nbsp;$this-&amp;gt;last();\r\n		$page&amp;nbsp;.=&amp;nbsp;$this-&amp;gt;prev();\r\n		$page&amp;nbsp;.=&amp;nbsp;$this-&amp;gt;next();\r\n		return&amp;nbsp;$page;&amp;nbsp;&amp;nbsp;\r\n	}\r\n\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', 'PHP工具类之分页类', 1491806877, 1, 0, 0, 0, 1),
(9, 2, 'PHP实现冒泡排序(Bubble Sort)', '201704/thumb_1491807103_1836760827.jpg', '&lt;p&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;PHP实现冒泡排序(Bubble Sort)&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;相邻的两个数之间进行比较，大数向后移动，移动一轮之后，保证最大的数在数列的最后端；再按相同的做法，比较剩余的数，直至数列的数全部比较完。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;原理解析：&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;1、比较相邻的两个元素，如果第一个比第二个大，就交换位置。这里可以使用第3个变量进行交换。&amp;lt;!--more--&amp;gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;2、对每一对相邻的元素做同样的操作，从开始的第一对到结尾的最后的一对。最后的那个元素应该是最大的数。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;3、针对所有的元素重复上面的操作，最后一个元素除外，因为不能比较了。所以比较的轮数应该是总数-1.&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;4、在一轮中比较的次数变化：假设有10个数，那么要比较9轮，在第一轮中要比较9次，第二轮中还剩9个数，所以比较8次，第3轮中还剩8个数，所以比较7次。最后得出，一轮中比较的次数为数据总数-轮数。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;下面来贴出代码：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;/*\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;冒泡排序\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;@param&amp;nbsp;$array&amp;nbsp;待排序的列表\r\n&amp;nbsp;*/\r\nfunction&amp;nbsp;bubbleSort(&amp;amp;$array){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//双层循环完成，外层控制轮数，内层控制比较次数\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for($i=1,&amp;nbsp;$num=count($array);&amp;nbsp;$i&amp;lt;$num;&amp;nbsp;$i++){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for($j=0;&amp;nbsp;$j&amp;lt;$num-$i;&amp;nbsp;$j++){&amp;nbsp;//比较j与j+1&amp;nbsp;if($array[$j]&amp;nbsp;&amp;gt;&amp;nbsp;$array[$j+1]){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//进行交换\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$third&amp;nbsp;=&amp;nbsp;$array[$j];\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$array[$j]&amp;nbsp;=&amp;nbsp;$array[$j+1];\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$array[$j+1]&amp;nbsp;=&amp;nbsp;$third;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$array;\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', 'PHP实现冒泡排序(Bubble Sort)', 1491807103, 1, 0, 0, 0, 1),
(10, 2, 'Yii2中urlManager的简单配置说明', '', '&lt;p&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;Yii2可以非常简单地实现网址伪静态的需求，通过配置组件 urlManager 来实现。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;1、首先在配置文件中的组件配置 components 中添加 urlManager 的配置&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;#39;urlManager&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;[\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;enablePrettyUrl&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;true,&amp;nbsp;&amp;nbsp;//是否美化&amp;nbsp;Url\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;showScriptName&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;false,&amp;nbsp;&amp;nbsp;//是否显示脚本名&amp;nbsp;index.php\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;suffix&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;#39;.html&amp;#39;,&amp;nbsp;//后缀名\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;rules&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;[\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;&amp;lt;controller:\\w+&amp;gt;/&amp;lt;action:\\w+&amp;gt;&amp;#39;=&amp;gt;&amp;#39;&amp;lt;controller&amp;gt;/&amp;lt;action&amp;gt;&amp;#39;,&amp;nbsp;//网址匹配规则\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;],\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;],&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;2、开启服务器的重写功能。这里用 apache 举例。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;首先开启 apache 的 rewrite 重写模块，然后在网站根目录里的添加 .htaccess 配置文件，并且输入以下内容。&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;RewriteEngine&amp;nbsp;on\r\n#if&amp;nbsp;a&amp;nbsp;directory&amp;nbsp;or&amp;nbsp;a&amp;nbsp;file&amp;nbsp;exists,&amp;nbsp;use&amp;nbsp;it&amp;nbsp;directly\r\nRewriteCond&amp;nbsp;%{REQUEST_FILENAME}&amp;nbsp;!-f\r\nRewriteCond&amp;nbsp;%{REQUEST_FILENAME}&amp;nbsp;!-d\r\n#Otherwise&amp;nbsp;forward&amp;nbsp;it&amp;nbsp;to&amp;nbsp;index.php\r\nRewriteRule&amp;nbsp;.&amp;nbsp;index.php&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;3、然后对 rules 配置进行编辑，以下举例一些常用的。&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;#39;rules&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;[\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;为路由指定一个别名简化网址\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;reg&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;#39;user/register&amp;#39;,\r\n&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;加id参数，这里用到了一点点正则，\\d+在正则中表示至少一位的纯数字\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;article/&amp;lt;id:\\d+&amp;gt;&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;#39;article/view&amp;#39;,\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//标准的控制器/方法显示\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;&amp;lt;controller:\\w+&amp;gt;/&amp;lt;action:\\w+&amp;gt;&amp;#39;=&amp;gt;&amp;#39;&amp;lt;controller&amp;gt;/&amp;lt;action&amp;gt;&amp;#39;,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//加id参数\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;&amp;lt;controller:\\w+&amp;gt;/&amp;lt;action:\\w+&amp;gt;/&amp;lt;id:\\d+&amp;gt;&amp;#39;=&amp;gt;&amp;#39;&amp;lt;controller&amp;gt;/&amp;lt;action&amp;gt;&amp;#39;,\r\n&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;controller和action进行严格限制\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;&amp;lt;controller:(post|comment)&amp;gt;/&amp;lt;id:\\d+&amp;gt;/&amp;lt;action:(create|update|delete)&amp;gt;&amp;#39;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;#39;&amp;lt;controller&amp;gt;/&amp;lt;action&amp;gt;&amp;#39;,\r\n&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;包含了&amp;nbsp;HTTP&amp;nbsp;方法限定，用于restful风格的Web&amp;nbsp;Server\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;DELETE&amp;nbsp;&amp;lt;controller:\\w+&amp;gt;/&amp;lt;id:\\d+&amp;gt;&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;#39;&amp;lt;controller&amp;gt;/delete&amp;#39;,\r\n&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;配置Web&amp;nbsp;Server&amp;nbsp;,接收&amp;nbsp;*.cn09.com&amp;nbsp;域名的请求\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;http://&amp;lt;user:\\w+&amp;gt;.cn09.com/&amp;lt;lang:\\w+&amp;gt;/profile&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;#39;user/profile&amp;#39;,&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', 'Yii2中urlManager的简单配置说明', 1491807216, 1, 0, 0, 0, 1),
(11, 5, 'Linux 中 Vi 编辑器的简单操作', '201704/thumb_1491807325_1048393042.jpg', '&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;Linux 中 Vi 编辑器的简单操作&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;Vi 编辑器一共有3种模式：命名模式（默认），尾行模式，编辑模式。3种模式彼此需要切换。&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;一、进入 Vi 编辑器的的命令&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;vi&amp;nbsp; filename　　//打开或新建文件，并将光标置于第一行首&lt;br/&gt;vi&amp;nbsp; +n&amp;nbsp; filename　　//打开文件，并将光标置于第n行首&lt;br/&gt;vi&amp;nbsp; +&amp;nbsp; filename　　//打开文件，并将光标置于最后一行首&lt;br/&gt;vi&amp;nbsp; +/pattern&amp;nbsp; filename　　//打开文件，并将光标置于第一个与 pattern 匹配处&lt;br/&gt;vi&amp;nbsp; -r&amp;nbsp; filename　　//在上次使用 vi 编辑时发生系统崩溃，恢复 filename&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;二、Vi 3种模式的切换&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;一）、从命令模式到编辑模式&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;在命令模式下按以下命令将进入到编辑模式&lt;br/&gt;1、a　　在光标后进入编辑模式&lt;br/&gt;2、i　　在光标前进入编辑模式&lt;br/&gt;3、A　　在当前行尾进入编辑模式&lt;br/&gt;4、I　　在当前行首进入编辑模式&lt;br/&gt;5、o　　在当前行之下新开一行&lt;br/&gt;6、O　　在当前行之上新开一行&lt;br/&gt;7、s　　删除光标所对应的字符，进入编辑模式&lt;br/&gt;8、ns　　//删除 n 个字符后进入编辑模式&lt;br/&gt;9、S　　删除光标所在的行，然后进入编辑模式&lt;br/&gt;10、nS　　//删除 n 行后，进入编辑模式&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;三、命令模式下的操作&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;一）、光标的移动&lt;br/&gt;1、j　　光标向下移动一行&lt;br/&gt;2、k　　光标向上移动一行&lt;br/&gt;3、h　　光标向左移动一个字符&lt;br/&gt;4、l　　光标向右移动一个字符&lt;br/&gt;5、ctrl + b　　//光标向上移动一屏，向后翻页&lt;br/&gt;6、ctrl + f　　//光标向下移动一屏，向前翻页&lt;br/&gt;7、$　　//光标移动至行尾&lt;br/&gt;8、0（数字）　　//光标移动至行首&lt;br/&gt;9、gg　　//光标移动至文档开始处&lt;br/&gt;10、G　　//光标移动至文档尾部&lt;br/&gt;11、nG　　//光标移动到第 n 行&lt;br/&gt;12、w　　//移动到下个单词的首字母&lt;br/&gt;13、b　　//移动到本单词或者上个单词的首字母&lt;br/&gt;14、e　　//移动到本单词或者下个单词的尾字母&lt;br/&gt;15、{　　//光标移动到段落首部&lt;br/&gt;16、}　　//光标移动到段落尾部&lt;br/&gt;17、L　　//光标移动到当前屏幕尾部（不会走到下一屏）&lt;br/&gt;18、H　　//光标移动到当前屏幕首部&lt;br/&gt;19、M　　//光标移动至当前屏幕中间行&lt;br/&gt;20、space　　//光标向前移动一个字符&lt;br/&gt;21、Backspace　　//光标向后移动一个字符&lt;br/&gt;22、n+　　//光标下移 n 行&lt;br/&gt;23、n-　　//光标上移 n 行&lt;br/&gt;24、n$　　//光标移动至第 n 行尾&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;二）、文本内容的删除&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;1、x　　//删除光标所在位置的一个字符&lt;br/&gt;2、nx　　//删除光标所在位置起 n个字符&lt;br/&gt;3、dx　　//从当前字符删除到下个单词前，包括空格&lt;br/&gt;4、ndx　　//从当前字符（单词）起，删除n个单词，包括最后一个空格后面的空格&lt;br/&gt;5、dd　　//删除当前行&lt;br/&gt;6、ndd　　//删除当前行起向下 n 行&lt;br/&gt;7、J　　//删除光标所在行与下一行之间的空格，把光标行和下一行接在一起&lt;br/&gt;通用删除：d+光标移动距离&lt;br/&gt;8、d$　　//删除光标到行尾的内容&lt;br/&gt;9、d0　　//删除光标到行首的内容&lt;br/&gt;10、d}　　//删除从光标到段落尾部的内容&lt;br/&gt;11、d{　　//删除从光标到段落首部的内容&lt;br/&gt;12、dL　　//删除当前行至屏幕尾部的内容&lt;br/&gt;13、dH　　//删除当前行至屏幕首部的内容&lt;br/&gt;14、dG　　//删除当前行至文档尾部的内容&lt;br/&gt;15、d1G　　//删除当前行至文档首部的内容&lt;br/&gt;16、dnG　　//删除当前行至文档的第 n 行&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;三）、文本内容的复制与粘帖&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;1、yy　　复制当前行&lt;br/&gt;2、nyy　　复制从当前行开始向下的n行&lt;br/&gt;3、y+光标移动的距离，可以进行灵活复制 （可以参考删除命令）&lt;br/&gt;3、p　　在光标之后粘贴（光标当前行的下一行进行粘贴）&lt;br/&gt;4、P(大写)　　在光标之前粘贴&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;三）、其他命令模式下的操作&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;1、u　　//撤销修改或删除操作&lt;br/&gt;2、r+字符　　//对光标当前单个字符进行替换&lt;br/&gt;3、R　　//替换当前字符及其后的字符，直至按 ESC 键&lt;br/&gt;3、.　　//重复执行上个命令&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;四、尾行模式下的操作&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;一）、文件的保存与退出&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;1、：w　　//保存&lt;br/&gt;2、：w&amp;nbsp; filename　　//另存为到 filename 文件&lt;br/&gt;3、：wq　　//保存退出&lt;br/&gt;4、：q　　//退出&lt;br/&gt;5、：wq!　　//强制保存退出&lt;br/&gt;6、：q!　　//不保存强制退出&lt;br/&gt;7、：x　　//保存并退出 ，应该和 wq! 功能一样&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;二）、查找与替换&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;1）、/search　　正向查找字符串 search，按 n 键查找下一个，按 N 键查找上一个&lt;br/&gt;2）、?search　　反向查找字符串 search&lt;br/&gt;3）、s /search/replace　　替换当前行首次出现的 search&lt;br/&gt;4）、s /search/replace/g　　把当前光标所在行中的 search 替换成 replace，并把文档中其余的 search 高亮显示&lt;br/&gt;5）、%s /search/replace　　把文档中所有的 search 替换成 replace&lt;br/&gt;6）、m,n s /search/replace/g　　把文档中从第m行到第n行中的 search 替换成 replace ，将把文档中其余的 search 高亮显示&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;三）、尾行模式下的文本编辑（复制，删除）&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;1）、n1,n2 co n3　　//将 n1 行到 n2 行的内容复制到 n3 行下&lt;br/&gt;2）、n1,n2 m n3　　//将 n1 行到 n2 行的内容移动至 n3 行下&lt;br/&gt;3）、n1,n2 d　　//将 n1 行到 n2 行之间的内容删除&lt;br/&gt;：set nu/number　　//给文件每行内容设置编号&lt;br/&gt;：set&amp;nbsp; nonu/nonumber　　//取消设置编号&lt;br/&gt;：n　　光标跳转到第 n 行位置&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;五、编辑模式下的操作&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;1、ctrl+u　　//删除编辑模式下输入的文本内容&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', 'Linux 中 Vi 编辑器的简单操作', 1491807325, 1, 0, 0, 0, 1),
(12, 5, 'Centos 压缩、解压和打包命令', '201704/thumb_1491807439_663883695.jpg', '&lt;h2 style=&quot;padding: 0px; margin: 0px; font-weight: normal; font-size: 14px; font-family: Verdana; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;color: rgb(0, 0, 255);&quot;&gt;&lt;strong&gt;gzip命令&lt;/strong&gt;&lt;/span&gt;&lt;/h2&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;--功能说明：gz文件的压缩和解压缩。&lt;br/&gt;--命令格式：gzip [参数] &amp;lt;文件&amp;gt;&lt;br/&gt;--常用参数：&lt;br/&gt;-d　　解开压缩文件&lt;br/&gt;-l　　列出压缩文件的相关信息&lt;br/&gt;-q　　不显示警告信息&lt;br/&gt;-v　　显示执行过程&lt;br/&gt;-c　　把压缩后的文件输出到标准输出设备，不改变原文件&lt;br/&gt;--压缩率：压缩率是一个介于1-9的数值，默认值为6，数值越大压缩率越高。&lt;br/&gt;-best 与 -9 相同， -fast 与 -1 相同。&lt;br/&gt;--使用示例：&lt;br/&gt;gzip&amp;nbsp; filename　　//生成filename.gz的 压缩文件，原文件消失&lt;br/&gt;gzip&amp;nbsp; -d&amp;nbsp; filename.gz　　//解压缩filename.gz文件，原文件消失&lt;br/&gt;gzip&amp;nbsp; -c filename &amp;gt; filename.gz　　//不改变原文件&lt;/p&gt;&lt;h2 style=&quot;padding: 0px; margin: 0px; font-weight: normal; font-size: 14px; font-family: Verdana; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;color: rgb(0, 0, 255);&quot;&gt;&lt;strong&gt;bzip2命令&lt;/strong&gt;&lt;/span&gt;&lt;/h2&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;--功能说明：.bz2文件的压缩与解压缩&lt;br/&gt;--命令格式：bzip2&amp;nbsp; [参数]&amp;nbsp; &amp;lt;文件&amp;gt;&lt;br/&gt;--常用参数：&lt;br/&gt;-k　　保留原始文件，默认在压缩或解压缩后会删除原始的文件&lt;br/&gt;-v　　显示指令执行过程&lt;br/&gt;-d　　解压缩文件&lt;br/&gt;-c　　把压缩后的文件输出到标准输出设备，不改变原文件&lt;br/&gt;-f　　强行压缩文件&lt;br/&gt;-s　　降低程序执行时内存的使用量，但会加长执行时间。&lt;br/&gt;-t　　测试压缩文件是否正确无误&lt;br/&gt;--压缩等级：压缩等级是一个介于1-9的数值，指定压缩时的区块大小。&lt;br/&gt;-repetitive-best : 若文件中有重复出现的资料时，可利用此参数提高压缩效果。&lt;br/&gt;-repetitive-fast : 若文件中有重复出现的资料时，可利用此参数加快执行速度。&lt;br/&gt;--使用示例：&lt;br/&gt;bzip2&amp;nbsp; filename　　//压缩filename文件，生成filename.bz2文件，原文件消失&lt;br/&gt;bzip2&amp;nbsp; filename.bz2　　//解压缩filename.bz2，生成filename文件，原文件消失&lt;br/&gt;--提示： bzip2 以区块的方式来压缩文件，每个区块视为独立的单位。因此，当某一区块损坏时，便可利用 bzip2recover 命令试着将文件中的区块分隔开来，以便解缩正常的区块。通常只适用压缩文件很大的情况。&lt;/p&gt;&lt;h2 style=&quot;padding: 0px; margin: 0px; font-weight: normal; font-size: 14px; font-family: Verdana; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;color: rgb(0, 0, 255);&quot;&gt;&lt;strong&gt;Zip命令&lt;/strong&gt;&lt;/span&gt;&lt;/h2&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;--功能说明：zip文件的压缩&lt;br/&gt;--命令格式：zip&amp;nbsp; [参数]&amp;nbsp; &amp;lt;文件&amp;gt;&lt;br/&gt;--常用参数：&lt;br/&gt;-r　　递归压缩子目录下所有文件&lt;br/&gt;-d　　删除压缩文件中的某个文件&lt;br/&gt;-m　　向压缩文件中添加某个文件&lt;br/&gt;-t　　检查压缩文件是否完整&lt;br/&gt;--使用示例：&lt;br/&gt;zip&amp;nbsp; -r&amp;nbsp; mydata.zip&amp;nbsp; mydata　　//压缩 mydata 目录&lt;br/&gt;zip&amp;nbsp; -r&amp;nbsp; filename.zip&amp;nbsp; file1&amp;nbsp; file2&amp;nbsp;&amp;nbsp; file3&amp;nbsp; /usr/work/school　　//zip 命令能同时处理多个文件和目录，这个命令是 将file1 file2 file3 /usr/work/school 一起压缩放入 filename.zip 中&lt;br/&gt;zip&amp;nbsp; -d&amp;nbsp; mydata.zip&amp;nbsp; smart.txt　　//删除 mydata.zip 中的 smart.txt 文件&lt;br/&gt;zip&amp;nbsp; -m&amp;nbsp; mydata.zip&amp;nbsp; smart.txt　　//向 mydata.zip 中添加 smart.txt 文件&lt;/p&gt;&lt;h2 style=&quot;padding: 0px; margin: 0px; font-weight: normal; font-size: 14px; font-family: Verdana; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;color: rgb(0, 0, 255);&quot;&gt;&lt;strong&gt;unzip命名&lt;/strong&gt;&lt;/span&gt;&lt;/h2&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;--功能说明：zip文件的解压缩&lt;br/&gt;--命令格式：unzip&amp;nbsp; [参数]&amp;nbsp; &amp;lt;文件&amp;gt;&amp;nbsp; &amp;lt;目录&amp;gt;&lt;br/&gt;--常用参数：&lt;br/&gt;-o　　不提示的情况下覆盖文件&lt;br/&gt;-d　　指明文件将解压到某个目录下&lt;br/&gt;--使用示例：&lt;br/&gt;unzip&amp;nbsp; mydata.zip&amp;nbsp; -d&amp;nbsp;&amp;nbsp; mydata　　//将 mydata.zip 中的内容解压到 mydata 中去&lt;br/&gt;unzip mydata.zip　　//直接将 mydata.zip 解压到当前目录&lt;/p&gt;&lt;h2 style=&quot;padding: 0px; margin: 0px; font-weight: normal; font-size: 14px; font-family: Verdana; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;color: rgb(0, 0, 255);&quot;&gt;&lt;strong&gt;tar命令&lt;/strong&gt;&lt;/span&gt;&lt;/h2&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;--功能说明：文件的打包和解包&lt;br/&gt;--命令格式：tar&amp;nbsp; [参数]&amp;nbsp; &amp;lt;目录&amp;gt;&lt;br/&gt;--常用参数：&lt;br/&gt;-z　　使用gzip压缩文件或解压&lt;br/&gt;-Z　　用compress压缩文件或解压&lt;br/&gt;-j　　用bzip2压缩文件或解压&lt;br/&gt;-c　　用于创建一个新的存档文件，即打包&lt;br/&gt;-x　　从归档文件中恢复备份文件，即解包&lt;br/&gt;-v　　列出处理的详细信息&lt;br/&gt;-f filename　　使用filename指定存档文件名&lt;br/&gt;-t　　用于列出一个存档文件中的文件名&lt;br/&gt;--使用示例：&lt;br/&gt;tar&amp;nbsp; -cvf&amp;nbsp; my.tar&amp;nbsp; somedirname　　//将somedirname等多个文件打包到 my.tar中，原文件不变&lt;br/&gt;tar&amp;nbsp; -tf&amp;nbsp; my.tar　　//列出 my.tar 中的文件&lt;br/&gt;tar&amp;nbsp; -xvf　my.tar　　//将 my.tar 包中的文件解包出来,原文件不变&lt;br/&gt;tar&amp;nbsp; -zcvf　my.tar.gz&amp;nbsp;&amp;nbsp; somedirname 　　//将 somedirname等多个文件打包并使用 gzip进行压缩&lt;br/&gt;tar&amp;nbsp; -jcvf&amp;nbsp;&amp;nbsp; my.tar.bz2&amp;nbsp; somedirname　　//将 somedirname等多个文件打包并使用 bzip2进行压缩&lt;br/&gt;tar&amp;nbsp; -ztf&amp;nbsp; my.tar.gz　　//查看my.tar.gz 中的文件&lt;br/&gt;tar&amp;nbsp; -jtf&amp;nbsp; my.tar.bz2　　//查看 my.tar.bz2中的文件&lt;br/&gt;tar&amp;nbsp; -zxvf&amp;nbsp; my.tar.gz　　//对 my.tar.gz 进行解压缩并解包&lt;br/&gt;tar&amp;nbsp; -jxvf&amp;nbsp; my.tar.bz2　　//对 my.tar.bz2 进行解压缩并解包&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', 'Centos 压缩、解压和打包命令', 1491807439, 1, 0, 0, 0, 1);
INSERT INTO `tpcms_article` (`article_id`, `cate_id`, `title`, `titleimg`, `content`, `author`, `keywords`, `synopsis`, `newstime`, `clicks`, `is_new`, `is_hot`, `is_recommend`, `status`) VALUES
(13, 5, 'Linux用户和组的管理操作', '201704/thumb_1491807612_360014038.jpg', '&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;命令中涉及的单词和简写：&lt;br/&gt;用户：user；组：group ； 增加：add ；&amp;nbsp; 修改：modify&amp;nbsp; mod ;&amp;nbsp; 删除 : delete&amp;nbsp; del&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;color: rgb(0, 0, 255);&quot;&gt;&lt;strong&gt;增加用户&lt;/strong&gt;&lt;/span&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; /etc/passwd 存放系统用户信息(组编号、家目录等)&lt;br/&gt;useradd&amp;nbsp; 用户名&lt;br/&gt;useradd -g&amp;nbsp;&amp;nbsp; 编号&amp;nbsp;&amp;nbsp; 用户名&amp;nbsp;&amp;nbsp;&amp;nbsp; //增加用户的同时设置&amp;quot;组编号&amp;quot;，常用操作&lt;br/&gt;useradd -d&amp;nbsp; 目录名&amp;nbsp; 用户名&amp;nbsp; //增加用户的同时会设置&amp;quot;家目录&amp;quot;，目录没有会自动创建&lt;br/&gt;passwd&amp;nbsp; 用户名&amp;nbsp; //给一个用户设置密码，可以让其登录系统，密码按照提示输入两遍&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', 'Linux用户和组的管理操作', 1491807612, 1, 0, 0, 0, 1),
(14, 5, 'Linux设备的挂载与卸载', '201704/thumb_1491807737_2091357044.jpg', '&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;在Windows里边，给光驱放入光盘，系统G盘符会“自动”与光盘进行连接。&lt;br/&gt;在Liunx系统里边，给光驱放入光盘，系统并不会让我们使用这个光盘，因为他还没有“挂载”的过程。&lt;br/&gt;Liunx系统的光盘在 /dev/cdrom ,但是 cdrom 是硬件设备，我们不能直接读取，因为需要把这个硬件设备与我们硬盘里面具体的一个目录（Windows系统就是G盘符）给链接上。新建一个目录，让这个目录与 /dev/cdrom 联系，这个过程就是“挂载”。&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;挂载的基本命令： mount&amp;nbsp; 设备&amp;nbsp; 目录地址&lt;br/&gt;挂载光盘： mount&amp;nbsp; /dev/cdrom&amp;nbsp; 目录地址&lt;br/&gt;挂载U盘： mount&amp;nbsp; /dev/had1&amp;nbsp; 目录地址&lt;br/&gt;挂载硬盘 : mount&amp;nbsp; /dev/sda3&amp;nbsp; 目录地址&lt;br/&gt;卸载命令 ：&amp;nbsp; umount&amp;nbsp;&amp;nbsp; 设备（或者挂载点）&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', 'Linux设备的挂载与卸载', 1491807737, 1, 0, 0, 0, 1),
(15, 5, 'Linux中 ls 命令的使用语法', '201704/thumb_1491807819_1143107366.jpg', '&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;-功能说明：&lt;/strong&gt;显示指定工作目录下的内容&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;-命令格式:&lt;/strong&gt;ls [-alrtAFR] [name...]&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;-常用参数：&lt;/strong&gt;&lt;br/&gt;-a或--all &amp;nbsp; 显示所有文件及目录(ls内定将文件名或目录名称开头为&amp;#39;.&amp;#39;的视为隐藏文档，不会列出)&lt;br/&gt;-l&amp;nbsp; 显示是文件还是目录，它的大小、修改日期和时间、文件或目录的名字以及文件的属主和它的权限。&lt;br/&gt;-h或--human-readable&amp;nbsp;&amp;nbsp; 用&amp;quot;K&amp;quot;,&amp;quot;M&amp;quot;,&amp;quot;G&amp;quot;来显示文件和目录的大小。&lt;br/&gt;-F或--classify&amp;nbsp; 在执行文件后加&amp;quot;*&amp;quot;，目录后加上&amp;quot;/&amp;quot;，Socket后面加上&amp;quot;=&amp;quot;，符号连接后面加上&amp;quot;@&amp;quot;，管道名称后面加上&amp;quot;|&amp;quot;。&lt;br/&gt;-r或--reverse&amp;nbsp; 反向排序。&lt;br/&gt;-R或--recursive&amp;nbsp; 递归处理，将指定目录下的所有文件及子目录一并处理。&lt;br/&gt;-t&amp;nbsp; 用文件和目录的更改时间排序。&lt;br/&gt;-S&amp;nbsp; 用文件和目录的大小排序。&lt;br/&gt;-i或--inode&amp;nbsp; 显示文件和目录的inode编号。&lt;br/&gt;--version&amp;nbsp; 显示版本信息。&lt;br/&gt;--help&amp;nbsp; 在线帮助。&lt;br/&gt;-d或--directory&amp;nbsp; 显示目录名称而非其内容。&lt;br/&gt;-n或--numeric-uid-gid&amp;nbsp; 以用户识别码和群组识别码替代其名称。&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;-使用实例：&lt;/strong&gt;&lt;br/&gt;ls&amp;nbsp; //不带任何选项列出文件&lt;br/&gt;ls -l&amp;nbsp; //列出文件列表，显示大小、修改日期、时间、文件、目录名字、权限等信息&lt;br/&gt;ls -a&amp;nbsp; //浏览所有文件，包括隐藏文件&lt;br/&gt;ls -lh&amp;nbsp; //以易读方式列出文件信息&lt;br/&gt;ls -F&amp;nbsp; //以尾部以‘/’字符结尾的方式列出文件和目录&lt;br/&gt;ls -r&amp;nbsp; //倒序列出文件&lt;br/&gt;ls -R&amp;nbsp; //递归列出子目录&lt;br/&gt;ls -ltr&amp;nbsp; //以修改时间倒叙列出&lt;br/&gt;ls -lS&amp;nbsp; //按文件大小排序&lt;br/&gt;ls -i&amp;nbsp; //显示文件或目录的索引节点号&lt;br/&gt;ls --version&amp;nbsp; //显示ls命令的版本&lt;br/&gt;ls -l /tmp&amp;nbsp; //列出指定目录的信息&lt;br/&gt;ls -ld /tmp/&amp;nbsp; //只显示/tmp/目录本身的信息&lt;br/&gt;ls -n&amp;nbsp; //显示文件的UID和GID&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', 'Linux中 ls 命令的使用语法', 1491807819, 1, 0, 0, 0, 1),
(16, 5, 'Linux中 cd 命令的使用语法', '201704/thumb_1491807949_2116547239.jpg', '&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;-功能说明：&lt;/strong&gt;切换目录&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;-命令格式：&lt;/strong&gt;cd [目标目录]&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;-使用实例：&lt;/strong&gt;&lt;br/&gt;cd /&amp;nbsp; //进入系统根目录&lt;br/&gt;cd ..&amp;nbsp; //切换到上级目录&lt;br/&gt;cd&amp;nbsp; //进入当前用户主目录&lt;br/&gt;cd ~ //进入当前用户主目录&lt;br/&gt;cd /tmp/&amp;nbsp; //进入 tmp 指定目录&lt;br/&gt;cd -&amp;nbsp; //返回进入此目录之前所在的目录&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', 'Linux中 cd 命令的使用语法', 1491807949, 1, 0, 0, 0, 1),
(17, 3, 'Mysql数据库定义语句', '201704/thumb_1491808204_86470571.jpg', '&lt;p&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;1、创建数据库&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;CREATE&amp;nbsp;DATABASE&amp;nbsp;数据库名&amp;nbsp;charset&amp;nbsp;utf8;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;2、删除数据库&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;DROP&amp;nbsp;DATABASE&amp;nbsp;数据库名;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;3、创建表语句&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;CREATE&amp;nbsp;TABLE&amp;nbsp;`workers_info`&amp;nbsp;(&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;`id`&amp;nbsp;int(11)&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;AUTO_INCREMENT,&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;`workername`&amp;nbsp;varchar(20)&amp;nbsp;NOT&amp;nbsp;NULL,&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;`sex`&amp;nbsp;enum(F,M,S),&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;`salary`&amp;nbsp;int(11)&amp;nbsp;DEFAULT&amp;nbsp;&amp;#39;0&amp;#39;,&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;`email`&amp;nbsp;&amp;nbsp;varchar(30),&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;`EmployedDates`&amp;nbsp;&amp;nbsp;date,&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;`department`&amp;nbsp;&amp;nbsp;varchar(30),&amp;nbsp;&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;PRIMARY&amp;nbsp;KEY&amp;nbsp;(`id`)&amp;nbsp;&amp;nbsp;\r\n)&amp;nbsp;ENGINE=MyISAM&amp;nbsp;&amp;nbsp;DEFAULT&amp;nbsp;CHARSET=utf8;&lt;/pre&gt;&lt;p&gt;4、&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;新增列信息&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;ALTER&amp;nbsp;TABLE&amp;nbsp;表名&amp;nbsp;ADD&amp;nbsp;[COLUMN]&amp;nbsp;列字段定义&amp;nbsp;[FIRST&amp;nbsp;|&amp;nbsp;AFTER&amp;nbsp;列名称]&amp;nbsp;\r\nALTER&amp;nbsp;TABLE&amp;nbsp;product&amp;nbsp;ADD&amp;nbsp;pro_name&amp;nbsp;varchar(50)&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;DEFAULT&amp;nbsp;&amp;#39;&amp;#39;&amp;nbsp;AFTER&amp;nbsp;id;&lt;/pre&gt;&lt;p&gt;5、&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;修改列信息&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;ALTER&amp;nbsp;TABLE&amp;nbsp;表名&amp;nbsp;MODIFY&amp;nbsp;[COLUMN]&amp;nbsp;列字段定义&amp;nbsp;[FIRST&amp;nbsp;|&amp;nbsp;AFTER&amp;nbsp;列名称]&amp;nbsp;\r\nALTER&amp;nbsp;TABLE&amp;nbsp;product&amp;nbsp;MODIFY&amp;nbsp;status&amp;nbsp;TINYINT(1)&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;DEFAULT&amp;nbsp;1&amp;nbsp;;&lt;/pre&gt;&lt;p&gt;6、&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;删除列信息&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;ALTER&amp;nbsp;TABLE&amp;nbsp;表名&amp;nbsp;DROP&amp;nbsp;&amp;nbsp;[column]&amp;nbsp;&amp;nbsp;列名;&amp;nbsp;\r\nALTER&amp;nbsp;TABLE&amp;nbsp;product&amp;nbsp;&amp;nbsp;DROP&amp;nbsp;&amp;nbsp;&amp;nbsp;cat_id;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', 'Mysql数据库定义语句', 1491808204, 1, 0, 0, 0, 1),
(18, 4, 'html5兼容处理(ie8以下版本兼容问题)', '', '&lt;p&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;(我们在测试ie 的兼容的时候，有一个叫做ietest 的软件，这个软件可以模拟ie6-ie11)&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;在不支持HTML5新标签的浏览器里，会将这些新的标签解析成行内元素(inline)对待，所以我们只需要将其转换成块元素(block)即可使用，但是在IE9版本以下，并不能正常解析这些新标签，但是却可以识别通过document.createElement(&amp;#39;tagName&amp;#39;)创建的自定义标签，于是我们的解决方案就是将HTML5的新标签全部通过document.createElement(&amp;#39;tagName&amp;#39;)来创建一遍，这样IE低版本也能正常解析HTML5新标签了，在实际开发中我们更多采用的是通过检测IE浏览器的版本来加载三方的一个JS库来解决兼容问题。&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;&amp;lt;!--条件注释，只有ie能识别--&amp;gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;!--[if&amp;nbsp;lte&amp;nbsp;ie&amp;nbsp;8]&amp;gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;script&amp;nbsp;src=&amp;quot;html5shiv.min.js&amp;quot;&amp;gt;&amp;lt;/script&amp;gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;![endif]--&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', 'html5兼容处理(ie8以下版本兼容问题)', 1491808390, 1, 0, 0, 0, 1),
(19, 4, 'html5表单新增表单类型，表单元素，表单属性', '', '&lt;p&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;3.1输入类型 (表单类型，表单元素，表单属性,表单事件.)&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;email 输入email格式&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;tel 手机号码&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;url 只能输入url格式&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;number 只能输入数字&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;search 搜索框&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;range 范围 滑动条&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;color 拾色器&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;time 时间&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;date 日期 不是绝对的&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;--datetime 时间日期&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;month 月份&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;week 星期&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;部分类型是针对移动设备生效的，且具有一定的兼容性，在实际应用当中可选择性的使用。&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: Verdana; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;3.2表单元素（标签）&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;lt;datalist&amp;gt; 数据列表与input 配合使用&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;&amp;lt;input&amp;nbsp;type=”text”&amp;nbsp;list=”data”&amp;gt;\r\n\r\n&amp;lt;datalist&amp;nbsp;id=”data”&amp;gt;\r\n\r\n&amp;lt;option&amp;gt;男&amp;lt;/option&amp;gt;\r\n\r\n&amp;lt;option&amp;gt;女&amp;lt;/option&amp;gt;\r\n\r\n&amp;lt;option&amp;gt;不详&amp;lt;/option&amp;gt;\r\n\r\n&amp;lt;/datalist&amp;gt;&lt;/pre&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;lt;keygen&amp;gt; &amp;nbsp;生成加密字符串&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;keygen 元素的作用是提供一种验证用户的可靠方法。&lt;br/&gt;keygen 元素是密钥对生成器（key-pair generator）。当提交表单时，会生成两个键， 一个是私钥，一个公钥。&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;私钥（private key）存储于客户端，公钥（public key）则被发送到服务器。公钥可用于之后验证用户的客户端证书（client certificate）。&lt;br/&gt;3.3表单属性&lt;br/&gt;placeholder 占位符&lt;br/&gt;autofocus 获取焦点&lt;br/&gt;multiple 文件上传多选或多个邮箱地址&lt;br/&gt;autocomplete 自动完成，用于表单元素，也可用于表单自身(on/off)&lt;br/&gt;form 指定表单项属于哪个form，处理复杂表单时会需要&lt;br/&gt;novalidate 关闭验证，可用于&amp;lt;form&amp;gt;标签&lt;br/&gt;required 必填项&lt;br/&gt;pattern 正则表达式 验证表单&lt;/p&gt;&lt;p style=&quot;padding: 15px 0px 0px; margin-top: 0px; margin-bottom: 0px; font-family: Verdana; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;3.4表单事件&lt;br/&gt;oninput 用户输入内容时触发，可用于移动端输入字数统计&lt;br/&gt;oninvalid 验证不通过时触发&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '', '', 'html5表单新增表单类型，表单元素，表单属性', 1491808450, 1, 0, 0, 0, 1);

